import React from 'react';

const Experience = () => {
    const experiences = [
        {
            title: "Android Developer",
            company: "Bangkit Capstone Project",
            period: "2024 - Present",
            description: "Contributing to innovative Android development projects as part of the Bangkit Capstone program. Working with a collaborative team to develop mobile applications using modern Android development practices.",
            technologies: ["Android", "Kotlin", "Firebase", "Material Design"],
            type: "project"
        },
        {
            title: "Software Engineering Student",
            company: "Self-Directed Learning",
            period: "2023 - Present",
            description: "Continuously learning and improving skills in software development, focusing on mobile application development and modern programming practices.",
            technologies: ["Java", "Kotlin", "Android Studio", "Git"],
            type: "education"
        }
    ];

    return (
        <section id="experience" className="py-20 bg-white">
            <div className="container mx-auto px-4">
                <h2 className="text-4xl font-bold text-center mb-12 text-gray-800">Experience & Education</h2>

                <div className="max-w-4xl mx-auto">
                    <div className="relative">
                        {/* Timeline line */}
                        <div className="absolute left-8 top-0 bottom-0 w-0.5 bg-blue-200"></div>

                        {experiences.map((exp, index) => (
                            <div key={index} className="relative mb-12 ml-16">
                                {/* Timeline dot */}
                                <div className="absolute -left-10 top-6 w-4 h-4 bg-blue-600 rounded-full border-4 border-white shadow-lg"></div>

                                <div className="bg-gray-50 p-6 rounded-lg shadow-md">
                                    <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start mb-3">
                                        <div>
                                            <h3 className="text-xl font-semibold text-gray-800">{exp.title}</h3>
                                            <p className="text-blue-600 font-medium">{exp.company}</p>
                                        </div>
                                        <span className="text-gray-500 text-sm mt-1 sm:mt-0">{exp.period}</span>
                                    </div>

                                    <p className="text-gray-600 mb-4 leading-relaxed">{exp.description}</p>

                                    <div className="flex flex-wrap gap-2">
                                        {exp.technologies.map((tech, techIndex) => (
                                            <span key={techIndex} className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
                                                {tech}
                                            </span>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Experience;
