import React, { useEffect, useRef, useState } from 'react';

const Skills = () => {
    const [isVisible, setIsVisible] = useState(false);
    const skillsRef = useRef(null);

    const skills = [
        {
            category: "Mobile Development",
            items: [
                { name: "Android (Java/Kotlin)", level: 85 },
                { name: "Android Studio", level: 90 },
                { name: "Firebase", level: 75 },
                { name: "SQLite", level: 70 }
            ]
        },
        {
            category: "Programming Languages",
            items: [
                { name: "Java", level: 85 },
                { name: "Kotlin", level: 80 },
                { name: "JavaScript", level: 70 },
                { name: "Python", level: 65 }
            ]
        },
        {
            category: "Tools & Technologies",
            items: [
                { name: "Git/GitHub", level: 85 },
                { name: "REST APIs", level: 75 },
                { name: "Material Design", level: 80 },
                { name: "MVVM Architecture", level: 70 }
            ]
        },
        {
            category: "Other Skills",
            items: [
                { name: "Problem Solving", level: 90 },
                { name: "Team Collaboration", level: 85 },
                { name: "UI/UX Principles", level: 70 },
                { name: "Agile Development", level: 75 }
            ]
        }
    ];

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setIsVisible(true);
                }
            },
            { threshold: 0.3 }
        );

        if (skillsRef.current) {
            observer.observe(skillsRef.current);
        }

        return () => {
            if (skillsRef.current) {
                observer.unobserve(skillsRef.current);
            }
        };
    }, []);

    return (
        <section id="skills" className="py-20 bg-gray-50" ref={skillsRef}>
            <div className="container mx-auto px-4">
                <h2 className="text-4xl font-bold text-center mb-12 text-gray-800">Skills & Technologies</h2>

                <div className="max-w-6xl mx-auto grid md:grid-cols-2 gap-8">
                    {skills.map((skillCategory, index) => (
                        <div key={index} className="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300">
                            <h3 className="text-xl font-semibold mb-6 text-gray-800">{skillCategory.category}</h3>
                            <div className="space-y-4">
                                {skillCategory.items.map((skill, skillIndex) => (
                                    <div key={skillIndex}>
                                        <div className="flex justify-between mb-2">
                                            <span className="text-gray-700 font-medium">{skill.name}</span>
                                            <span className="text-gray-500">{skill.level}%</span>
                                        </div>
                                        <div className="w-full bg-gray-200 rounded-full h-2">
                                            <div 
                                                className={`bg-blue-600 h-2 rounded-full transition-all duration-1000 ease-out ${isVisible ? 'opacity-100' : 'opacity-0'}`}
                                                style={{ 
                                                    width: isVisible ? `${skill.level}%` : '0%',
                                                    transitionDelay: `${skillIndex * 100}ms`
                                                }}
                                            ></div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Skills;
